@Preview(
 name = "print",
 device = "spec:width=1080dp, height=1920dp",
 showSystemUi = true
)
@SuppressLint("UnusedBoxWithConstraintsScope")
@Composable
fun TestPrintScreen(
 onPrintAsText: (String) -> Unit = {},
 onPrintAsImage: (String) -> Unit = {}
){
 Scaffold(
 modifier = Modifier.fillMaxSize()
 ) { innerPadding ->
 BoxWithConstraints(
 modifier = Modifier
 .background(Color.White)
 .fillMaxSize()
 .padding(innerPadding)
 ) {

 val logMessages = remember { mutableListOf<String>() }
 val printValue = remember { mutableStateOf("") }

 val maxHeightInDp = with(LocalDensity.current) { maxHeight }
 val maxWidthInDp = with(LocalDensity.current) { maxWidth }

 ConstraintLayout(
 modifier = Modifier
 .height(maxHeightInDp)
 .padding(10.dp)
 ) {
 val (topView, bottomView) = createRefs()

 Column(
 modifier = Modifier
 .fillMaxWidth()
 .height(maxHeightInDp * 0.45f)
 .constrainAs(topView){
 start.linkTo(parent.start)
 end.linkTo(parent.end)
 top.linkTo(parent.top)
 }
 ) {
 Text(
 text = "Input Text To Print:",
 color = Color.Black,
 fontSize = (maxWidthInDp.value * 0.05f).sp
 )

 TextField(
 value = printValue.value,
 onValueChange = { newValue ->
 if (newValue.length <= 4) {
 printValue.value = newValue
 }
 },
 modifier = Modifier
 .fillMaxWidth()
 .height(maxHeightInDp * 0.08f)
 .padding(vertical = 10.dp),
 placeholder = {
 Text(
 text = "Enter up to 4 digits",
 fontSize = (maxWidthInDp.value * 0.04f).sp
 )
 },
 keyboardOptions = KeyboardOptions(keyboardType = KeyboardType.Text),
 textStyle = TextStyle(
 fontSize = (maxWidthInDp.value * 0.05f).sp,
 color = Color.Black
 )
 )

 Spacer(Modifier.height(10.dp))
 ElevatedButton(
 onClick = { /*TODO*/ },
 modifier = Modifier
 .fillMaxWidth()
 .height(maxHeightInDp * 0.05f)
 ) {
 Text(
 text = "Print as Text",
 fontSize = (maxWidthInDp.value * 0.045f).sp
 )
 }

 Spacer(Modifier.height(18.dp))
 ElevatedButton(
 onClick = { /*TODO*/ },
 modifier = Modifier
 .fillMaxWidth()
 .height(maxHeightInDp * 0.05f)
 ) {
 Text(
 text = "Print as Image",
 fontSize = (maxWidthInDp.value * 0.045f).sp
 )
 }
 }

 Column(
 modifier = Modifier
 .fillMaxWidth()
 .height(maxHeightInDp * 0.5f)
 .constrainAs(bottomView){
 start.linkTo(parent.start)
 end.linkTo(parent.end)
 bottom.linkTo(parent.bottom)
 }
 ) {
 Text(
 text = "Log",
 color = Color.Black,
 fontSize = (maxWidthInDp.value * 0.05f).sp
 )
 Box(
 modifier = Modifier
 .fillMaxSize()
 .clip(
 RoundedCornerShape(
 20.dp
 )
 )
 .border(
 width = 2.dp,
 color = Color.Gray,
 shape = RoundedCornerShape(
 20.dp
 )
 )
 .padding((maxWidthInDp.value * 0.015f).dp)
 ){
 LazyColumn {
 items(logMessages.size){
 LogView(logMessages[it])
 }
 }
 }
 }
 }

 }
